#pragma config(Sensor, S1,     line,           sensorEV3_Color)
#pragma config(Motor,  motorB,          left,          tmotorEV3_Large, PIDControl, driveLeft, encoder)
#pragma config(Motor,  motorC,          right,         tmotorEV3_Large, PIDControl, driveRight, encoder)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#define LS_THRESHOLD 20

task main() {
	while (true) {
		// Charge!!!
		setMotorSpeed(motorB, 100);
		setMotorSpeed(motorC, 100);

		// Wait until we hit the line...
		while (getColorReflected(line) > LS_THRESHOLD);

		// Reverse away from the line...
		setMotorSpeed(motorB, -100);
		setMotorSpeed(motorC, -100);
		sleep(500);

		// ... and then turn away.
		setMotorSpeed(motorB, 0);
		setMotorSpeed(motorC, -100);
		sleep(500);
	}
}
